import React from 'react';
import './Login.css'; 


const Login = () => {
    return (
      <div className="login-container">
        <div className="login-box">
          <div className="left-side">
            {/* Conteúdo da parte esquerda (azul) */}
            <h2 className="text-white text-2xl font-bold mb-6">Esquerda</h2>
          </div>
          <div className="right-side">
            {/* Conteúdo da parte direita (branca) */}
            <h2 className="text-2xl font-bold mb-6">Direita</h2>
            <form>
              <label htmlFor="username" className="mb-2 block">Usuário:</label>
              <input type="text" id="username" name="username" className="p-2 mb-4 w-full" />
  
              <label htmlFor="password" className="mb-2 block">Senha:</label>
              <input type="password" id="password" name="password" className="p-2 mb-6 w-full" />
  
              <button type="submit" className="bg-blue-500 text-white p-2 rounded cursor-pointer hover:bg-blue-700">Entrar</button>
            </form>
          </div>
        </div>
      </div>
    );
  };
  
  
export default Login;
